sys_script_include.script:
function validateEmailAddress(str) {
	// guarantee str is a string
	str = str +  '';

	//Compare str against allowed syntax as specified in the regular expession
	// if str has allowed syntax, return rue, else return false
	if(str.match(/^(([^<>()\[\]\\.,;:\s@"]+(\.[^<>()\[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/)) {
		return true;
	} else {
		return false;
	}
}
